<?php/** * The Template for displaying all single posts. * * @package zerif */get_header(); ?></header> <!-- / END HOME SECTION  --><div id="content" class="site-content">	<div id="primary" class="content-area">		<main id="main" class="site-main" role="main">				<?php 					while ( have_posts() ) : the_post(); 							$t = "single";							foreach( (array) get_the_category() as $cat ) {																if ( file_exists(get_template_directory() . "/content-".$cat->slug.".php") ){									$t = $cat->slug;								}							} 													 get_template_part( 'content', $t );						 						 // removido para parar de exibir proximo ou anterior zerif_post_nav(); 					 						// If comments are open or we have at least one comment, load up the comment template						if ( comments_open() || '0' != get_comments_number() ) :							comments_template('');						endif;					endwhile; // end of the loop. ?>				</div>			</div>			</div> <!-- whiteblock container leftwrapper -->		</main><!-- #main -->	</div><!-- #primary --><?php get_footer(); ?>